pragma solidity ^0.5.0;

contract CertificateList {
    uint public certificateCount;
    address owner;

    constructor() public {
        owner = msg.sender;
        //default
        createCertificate("Heisenberg", "Bachelor of Mechanical Engineering",
                                "Konkon Gyanpith College Of Engineering", "First Class With Distinction"); //Default
    }

    modifier _ownerOnly() {
      require(msg.sender == owner, "Not authorized.");
      _;
    }

    struct Data {
        string name;
        string branch;
        string grade;
        string college;
    }

    mapping(uint => Data) public certificates;

    function createCertificate
    (string memory _name, string memory _branch, string memory _college, string memory _grade)
    public
    _ownerOnly
    {
        certificateCount++;
        certificates[certificateCount] = Data(_name, _branch, _grade, _college);
    }
}
